                          &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; self.val_dataloader &lt;span class=&#34;keyword&#34;&gt;is&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;not&lt;/span&gt; &lt;span class=&#34;literal&#34;&gt;None&lt;/span&gt; &lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt; &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;prepare_model&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;self, model&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        model.trainer = self&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        model.board.xlim = [&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;, self.max_epochs]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.model = model&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;fit&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;self, model, data&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.prepare_data(data)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.prepare_model(model)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.optim = model.configure_optimizers()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.epoch = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.train_batch_idx = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        self.val_batch_idx = &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;for&lt;/span&gt; self.epoch &lt;span class=&#34;keyword&#34;&gt;in&lt;/span&gt; &lt;span class=&#34;built_in&#34;&gt;range&lt;/span&gt;(self.max_epochs):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            self.fit_epoch()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;keyword&#34;&gt;def&lt;/span&gt; &lt;span class=&#34;title function_&#34;&gt;fit_epoch&lt;/span&gt;(&lt;span class=&#34;params&#34;&gt;self&lt;/span&gt;):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &lt;span class=&#34;keyword&#34;&gt;raise&lt;/span&gt; NotImplementedError&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;/p&gt;
&lt;p&gt;fit 接受 model（class Module）和 data（class DataModule），并迭代 max_epochs 次训练&lt;/p&gt;
